import React, { useState } from "react";

function RecipeCreate(props) {

  // need to store state better
  const [name, setName] = useState("");
  const [cuisine, setCuisine] = useState("");
  const [photo, setPhoto] = useState("");
  const [rating, setRating] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [preparation, setPreparation] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    const newRecipe = { name, cuisine, photo, rating, ingredients, preparation };
    console.log(newRecipe);
    props.onAddRecipe(newRecipe);


    // need to clear the form better, using spread operator and an object.
    setName("");
    setCuisine("");
    setPhoto("");
    setRating("");
    setIngredients("");
    setPreparation("");
  }

  return (
    <form name="create" onSubmit={handleSubmit}>
      <table>
        <tbody>
          <tr>
            <td>Name:</td>
            <td>
              <input value={name} onChange={(e) => setName(e.target.value)} />
            </td>
          </tr>
          <tr>
            <td>Cuisine:</td>
            <td>
              <input value={cuisine} onChange={(e) => setCuisine(e.target.value)} />
            </td>
          </tr>
          <tr>
            <td>Photo URL:</td>
            <td>
              <input value={photo} onChange={(e) => setPhoto(e.target.value)} />
            </td>
          </tr>
          <tr>
            <td>Rating:</td>
            <td>
              <input value={rating} onChange={(e) => setRating(e.target.value)} />
            </td>
          </tr>
          <tr>
            <td>Ingredients:</td>
            <td>
              <textarea value={ingredients} onChange={(e) => setIngredients(e.target.value)}></textarea>
            </td>
          </tr>
          <tr>
            <td>Preparation:</td>
            <td>
              <textarea value={preparation} onChange={(e) => setPreparation(e.target.value)}></textarea>
            </td>
          </tr>
          <tr>
            <td></td>
            <td>
              <button type="submit">Create</button>
            </td>
          </tr>
        </tbody>
      </table>
    </form>
  );
}

export default RecipeCreate;
