function RecipeList({ recipes, onDelete }) {
  return (
    <div>
      {recipes.map(recipe => (
        <div key={recipe.name}>
          <span>{recipe.name}</span>
          <button onClick={() => onDelete(recipe.name)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default RecipeList;
